#!/bin/bash

npm --json list -g --depth=0 --s | jq -r ".dependencies | keys[]" > $HOME/.paketrc

green='\033[0;32m'
reset='\033[0m'
bold='\033[1m'
boldreset='\033[0m' 


if [ "X$1" == "Xjson" ]; then
	output=$(npm --json list -g --depth=0 --s | jq -r '.dependencies[]')
	echo ${output}
else
	while read line
	do
	   	 name=${line} 
	     echo -e " ${bold}> ${boldreset}${green}${name}${reset}"
	done < $HOME/.paketrc
fi

